
public class Mathématiques {
	
	public static int mini(int a, int b) {
		if(a<b) return a;
		
		else return b;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Le minimum est : "+Mathématiques.mini(12,8));
		
		System.out.println("Le minimum est : "+mini(24,20));
	}

}
